MYSQL->query("DROP TABLE IF EXISTS SDG");

MYSQL->query("
CREATE TABLE IF NOT EXISTS SDG (
    ID int(10) unsigned NOT NULL AUTO_INCREMENT,
    title varchar(50) DEFAULT NULL,
    beschrijving varchar(256) DEFAULT NULL,
    image varchar(255) DEFAULT NULL,
    color varchar(50) DEFAULT NULL,
    extra_beschrijving varchar(1024) DEFAULT NULL,
    PRIMARY KEY (ID)
  ) ENGINE=InnoDB AUTO_INCREMENT=34 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_general_ci;
");

foreach($sdgs as $sdg) {
    $prep = MYSQL->prepare("INSERT INTO SDG VALUES (?, ?, ?, ?, ?, ?)");
    $prep->bind_param(
        "isssss", 
        $sdg["ID"],
        $sdg["title"],
        $sdg["beschrijving"],
        $sdg["image"],
        $sdg["color"],
        $sdg["extra_beschrijving"]
    );
    $row = $prep->execute();
    $prep->close();
}

var_dump(MYSQL->query("SELECT * FROM SDG")->fetch_all());